$(document).ready(function(){
	
	if($("a.buynow.btn-contact"))
	{
		$(".txtProductName input").val($(".product-name").text());
		
	}
	
    if ($(".news-detail-page .banner-ads-hidden").length > 0)
    {
        var $bannerAds = $(".news-detail-page .banner-ads-hidden");
        var content = $('.news-detail-page .content').find('p, br');
		
        var midLength = parseInt(content.length / 2);
        content.eq(midLength).after($($bannerAds));
        
        $($bannerAds).removeClass("banner-ads-hidden");
        $($bannerAds).addClass("banner-ads");
    }
});